import {
    PRODUCT_ADD,
    PRODUCT_ADD_SUCCESS,
    PRODUCT_ADD_ERROR
}
from "../actions"

export const productAdd = (product, history) => ({
    type: PRODUCT_ADD,
    payload: { product, history },
});
export const productAddSuccess = (product) => ({
    type: PRODUCT_ADD_SUCCESS,
    payload: product,
});
export const productAddError = (error) => ({
    type: PRODUCT_ADD_ERROR,
    error
});
