import React, { Fragment, useState, useEffect } from 'react';
import { connect } from "react-redux";
import * as Yup from 'yup';
import { Formik, Form, Field, FieldArray } from 'formik';

import {
    Row,
    Col,
    Card,
    CardBody,
    FormGroup,
    Label,
    Button,
    CardTitle,
} from 'reactstrap';

import IntlMessages from '../../helpers/IntlMessages';



const ProductAdd = () => {


    const initialValues = {
        name: "",
        type: "",



    }



    const onSubmit = (values, { resetForm, setSubmitting }) => {
        const payload = {
            ...values,

        };



    };







    return (


        <>
            <Row className="mb-4 /* d-flex justify-content-center */ my-auto">
                <Col xxs="12" md="6">
                    <Card>
                        <CardBody>
                            <CardTitle>
                                <IntlMessages id="Orange Details" />
                            </CardTitle>

                            <Formik
                                initialValues={initialValues}

                                /* validationSchema={SignupSchema} */
                                onSubmit={onSubmit}

                            >
                                {({
                                    handleSubmit,
                                    setFieldValue,
                                    setFieldTouched,
                                    handleChange,
                                    handleBlur,
                                    values,
                                    errors,
                                    touched,
                                    isSubmitting,
                                    onValueChange,


                                }) => (

                                    <Form className="av-tooltip tooltip-label-bottom">


                                        <Row>
                                            <Col xxs="12">
                                                <FormGroup className="form-group has-float-label">


                                                    <Label>
                                                        <IntlMessages id="Name/Type" />
                                                    </Label>
                                                    <Field className="form-control" name="name" value={values.name} />



                                                </FormGroup>
                                            </Col>

                                        </Row>
                                        <Row>
                                            <Col xxs="12">
                                                <FormGroup className="form-group has-float-label">


                                                    <Label>
                                                        <IntlMessages id="Rate (Rupees/Kg)" />
                                                    </Label>
                                                    <Field className="form-control" name="rate" type="number" value={values.rate} />



                                                </FormGroup>
                                            </Col>

                                        </Row>


                                        <Button color="primary" type="submit" className="mt-3" >
                                            {isSubmitting ? "loading" : "Add Product"}
                                        </Button>
                                    </Form>
                                )}
                            </Formik>
                        </CardBody>
                    </Card>
                </Col>


            </Row>


        </>
    );
}



export default ProductAdd
