import React, { Fragment, useState, useEffect } from 'react';
import { connect } from "react-redux";
import { useParams } from "react-router-dom"
import * as Yup from 'yup';
import { Formik, Form, Field, FieldArray } from 'formik';

import {
    Container,
    Row,
    Col,
    Card,
    CardBody,
    FormGroup,
    Label,
    Button,
    CardTitle,
} from 'reactstrap';
import { getSelectedProduct } from "../../redux/product/actions"
import IntlMessages from '../../helpers/IntlMessages';



const ProductEdit = ({ getSelectedProductAction, history }) => {

    const { id } = useParams()
    console.log("id for update", id)

    const getSelectedProduct = (id) => {
        getSelectedProductAction(id, history)

    }
    useEffect(() => {
        getSelectedProduct()
    }, [])


    const initialValues = {
        name: "",
        rate: "",
    }



    /*  const onSubmit = (values, { resetForm, setSubmitting }) => {

         productAddAction(values, history)
         resetForm()
         setSubmitting(false);

     }; */

    const SignupSchema = Yup.object().shape({
        name: Yup.string()
            .min(2, 'Too Short!')
            .max(50, 'Too Long!')
            .required('Please enter Orange Name / Type'),
        rate: Yup.string()
            .min(1, 'Very Less Price!')
            .max(50, 'Too Long!')
            .required('Please enter Price for Orange'),

    });



    return (


        <>
            <Container >
                <Row className="mb-4 d-flex justify-content-center align-items-center" style={{ height: "75vh" }}>
                    <Col xxs="12" md="6">
                        <Card>
                            <CardBody>
                                <CardTitle>
                                    <IntlMessages id="Orange Details" />
                                </CardTitle>

                                <Formik
                                    initialValues={initialValues}

                                    validationSchema={SignupSchema}
                                   /*  onSubmit={onSubmit} */

                                >
                                    {({
                                        handleSubmit,
                                        setFieldValue,
                                        setFieldTouched,
                                        handleChange,
                                        handleBlur,
                                        values,
                                        errors,
                                        touched,
                                        isSubmitting,
                                        onValueChange,


                                    }) => (

                                        <Form className="av-tooltip tooltip-label-bottom">


                                            <Row>
                                                <Col xxs="12">
                                                    <FormGroup className="form-group has-float-label">


                                                        <Label>
                                                            <IntlMessages id="Name/Type" />
                                                        </Label>
                                                        <Field className="form-control" name="name" value={values.name} />

                                                        {errors.name && touched.name ? (
                                                            <div className="invalid-feedback d-block">
                                                                {errors.name}
                                                            </div>
                                                        ) : null}

                                                    </FormGroup>
                                                </Col>

                                            </Row>
                                            <Row>
                                                <Col xxs="12">
                                                    <FormGroup className="form-group has-float-label">


                                                        <Label>
                                                            <IntlMessages id="Rate (Rupees/Kg)" />
                                                        </Label>
                                                        <Field className="form-control" name="rate" type="number" value={values.rate} />
                                                        {errors.rate && touched.rate ? (
                                                            <div className="invalid-feedback d-block">
                                                                {errors.rate}
                                                            </div>
                                                        ) : null}


                                                    </FormGroup>
                                                </Col>

                                            </Row>


                                            <Button color="primary" type="submit" className="mt-3" >
                                                {isSubmitting ? "loading" : "Add Product"}
                                            </Button>
                                        </Form>
                                    )}
                                </Formik>
                            </CardBody>
                        </Card>
                    </Col>


                </Row>
            </Container>


        </>
    );
}

const mapStateToProps = ({ sellerProduct }) => {
    const { selectedProduct, loading } = sellerProduct;
     console.log("selectedProduct", selectedProduct)

    return { selectedProduct, loading };


  };


export default connect(mapStateToProps, { getSelectedProductAction: getSelectedProduct })(ProductEdit)
