import React, { Component } from "react";

// MetisMenu
import MetisMenu from "metismenujs";
import { withRouter } from "react-router-dom";
import { Link } from "react-router-dom";

//i18n
import { withNamespaces } from 'react-i18next';

import { connect } from "react-redux";


class SidebarContent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            user_id: null
        };

    }
   

    userData = () => {
       /*  console.log(JSON.parse(localStorage.getItem('authUser')).role_id, "user role id") */
        this.setState({
           user_id: JSON.parse(localStorage.getItem('authUser')).role
        })
    }


    componentDidMount() {
        this.initMenu();
        this.userData()
    }

    componentDidUpdate(prevProps) {
        if (prevProps !== this.props) {

            if (this.props.type !== prevProps.type) {
                this.initMenu();
            }

        }
    }

    initMenu() {
        new MetisMenu("#side-menu");

        var matchingMenuItem = null;
        var ul = document.getElementById("side-menu");
        var items = ul.getElementsByTagName("a");
        for (var i = 0; i < items.length; ++i) {
            if (this.props.location.pathname === items[i].pathname) {
                matchingMenuItem = items[i];
                break;
            }
        }
        if (matchingMenuItem) {
            this.activateParentDropdown(matchingMenuItem);
        }
    }

    activateParentDropdown = item => {
        item.classList.add("active");
        const parent = item.parentElement;

        if (parent) {
            parent.classList.add("mm-active");
            const parent2 = parent.parentElement;

            if (parent2) {
                parent2.classList.add("mm-show");

                const parent3 = parent2.parentElement;

                if (parent3) {
                    parent3.classList.add("mm-active"); // li
                    parent3.childNodes[0].classList.add("mm-active"); //a
                    const parent4 = parent3.parentElement;
                    if (parent4) {
                        parent4.classList.add("mm-active");
                    }
                }
            }
            return false;
        }
        return false;
    };

    render() {
       const {user_id} = this.state

        return (
            <React.Fragment>
                <div id="sidebar-menu">

                    <ul className="metismenu list-unstyled" id="side-menu">
                        <li className="menu-title">{this.props.t('Menu')}</li>

                        <li>
                            <Link to="/dashboard" className="waves-effect">
                                {/*  <i className="ri-dashboard-line"></i><span className="badge badge-pill badge-success float-right">3</span> */}
                                <span className="ml-1">{this.props.t('Dashboard')}</span>
                            </Link>
                        </li>
                        {user_id ===1 ?
                        <>
                        <li>
                            <Link to="/users" className="waves-effect">
                                {/*  <i className="ri-dashboard-line"></i><span className="badge badge-pill badge-success float-right">3</span> */}
                                <span className="ml-1">{this.props.t('Users')}</span>
                            </Link>
                        </li>
                        <li>
                            <Link to="/roles" className="waves-effect">
                                {/*  <i className="ri-dashboard-line"></i><span className="badge badge-pill badge-success float-right">3</span> */}
                                <span className="ml-1">{this.props.t('Roles')}</span>
                            </Link>
                        </li>
                        </>
                        :""}
                        <li>
                            <Link to="/request" className="has-arrow waves-effect">
                                <i className="ri-list-check-2"></i>
                                <span className="ml-1">{this.props.t('Request')}</span>
                            </Link>
                            <ul className="sub-menu" aria-expanded="false">
                                <li><Link to="/request/checkin">{this.props.t('Check In')}</Link></li>
                                <li><Link to="/request/meal-request">{this.props.t('Meal Request')}</Link></li>
                                <li><Link to="/request/seat-request">{this.props.t('Seat Request')}</Link></li>
                                <li><Link to="/request/extra-baggage">{this.props.t('Extra Baggage')}</Link></li>
                                <li><Link to="/request/lost-baggage">{this.props.t('Lost Baggage')}</Link></li>
                                <li><Link to="/request/missed-flight">{this.props.t('Missed Flight Assistance')}</Link></li>
                                <li><Link to="/request/visa-documentation">{this.props.t('Visa Documentation')}</Link></li>
                                <li><Link to="/request/first-in-first-out">{this.props.t('First In First Out')}</Link></li>
                                <li><Link to="/request/special-request">{this.props.t('Special Request')}</Link></li>
                                <li><Link to="/request/covid-19">{this.props.t('Covid 19')}</Link></li>
                                <li><Link to="/request/flight-refund">{this.props.t('Flight Refund')}</Link></li>
                                <li><Link to="/request/Entry Requirement">{this.props.t('Entry Requirement')}</Link></li>
                            </ul>
                        </li>

                       

                    </ul>
                </div>
            </React.Fragment>
        );
    }
}



export default SidebarContent;
