import React from "react";
import {Link} from "react-router-dom"
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';
import {
    Row,
    Col,
    Card,
    CardBody,

    Button

} from 'reactstrap'

const expandRow = {
    renderer: row => (
        <>
            Action :
            <Link to="#" className="mr-3 text-primary"><i className="mdi mdi-eye font-size-18 ml-2"></i></Link>
            {/*  <Link to="#" className="text-danger" ><i className="mdi mdi-trash-can font-size-18"></i></Link> */}
        </>
    ),
    showExpandColumn: true,
    expandByColumnOnly: true
};

const data = {
    columns: [
        {
            dataField: 'id',
            text: 'No.'
        },
        {
            dataField: "orderId",
            text: "Trip ID"
        },
        {
            dataField: "date",
            text: "Trip Date"
        },
        {
            dataField: "billingName",
            text: "Trip Name"
        },
        {
            dataField: "billingName",
            text: "Company"
        },

    ],
    rows: [
        {
            id: 1,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1572</Link>,
            date: "04 Apr, 2020",
            billingName: "Walter Brown",
            total: "check in",
            status: <div className="badge badge-soft-success font-size-12">Pending</div>,
        },
        {
            id: 2,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1571</Link>,
            date: "03 Apr, 2020",
            billingName: "Jimmy Barker",
            total: "lost baggage",
            status: <div className="badge badge-soft-warning font-size-12">unpaid</div>,
        },
        {
            id: 3,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1570</Link>,
            date: "03 Apr, 2020",
            billingName: "Donald Bailey",
            total: "Meal Request",
            status: <div className="badge badge-soft-success font-size-12">Paid</div>,
        },
        {
            id: 4,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1569</Link>,
            date: "02 Apr, 2020",
            billingName: "Delhi",
            total: "visa request",
            status: <div className="badge badge-soft-success font-size-12">Paid</div>,
        },
        {
            id: 5,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1568</Link>,
            date: "04 Apr, 2020",
            billingName: "London",
            total: "Extra Baggages",
            status: <div className="badge badge-soft-danger font-size-12">Chargeback</div>,
        },
        {
            id: 6,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1567</Link>,
            date: "04 Apr, 2020",
            billingName: "Walter Brown",
            total: "$172",
            status: <div className="badge badge-soft-warning font-size-12">unpaid</div>,
        },
        {
            id: 7,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1566</Link>,
            date: "04 Apr, 2020",
            billingName: "Walter Brown",
            total: "$172",
            status: <div className="badge badge-soft-success font-size-12">Paid</div>,
        },
        {
            id: 8,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1565</Link>,
            date: "04 Apr, 2020",
            billingName: "Walter Brown",
            total: "$172",
            status: <div className="badge badge-soft-success font-size-12">Paid</div>,
        },
        {
            id: 9,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1564</Link>,
            date: "04 Apr, 2020",
            billingName: "Walter Brown",
            total: "$172",
            status: <div className="badge badge-soft-success font-size-12">Paid</div>,
        },
        {
            id: 10,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1563</Link>,
            date: "04 Apr, 2020",
            billingName: "Walter Brown",
            total: "$172",
            status: <div className="badge badge-soft-warning font-size-12">unpaid</div>,
        },
        {
            id: 11,
            orderId: <Link to="#" className="text-dark font-weight-bold">#NZ1562</Link>,
            date: "04 Apr, 2020",
            billingName: "Walter Brown",
            total: "$172",
            status: <div className="badge badge-soft-success font-size-12">Paid</div>,
        },
    ]
};


const Product = ({ history }) => {


    const options = {
        // pageStartIndex: 0,
        hideSizePerPage: false,
        hidePageListOnlyOnePage: false,
        sizePerPageList:
            [{
                text: '5th', value: 10
            }, {
                text: '10th', value: 10
            }, {
                text: 'All', value: data.rows.length
            }]

    };


    return (
        <>
            <Row className="mt-5">
                <Col xs="4">

                    <Button size="md" className="offset-md-1" onClick={() => history.push('/seller/product/add')}>Add New Product<sup> +</sup></Button>
                    {/* <Separator className="mb-5" /> */}
                </Col>
            </Row>
            <Row>

            <Col lg={12} className='mt-2'>
                                <Card>
                                    <CardBody>


                                        <h4 className="card-title mb-4">All Products</h4>

                                        <BootstrapTable
                                          keyField='id'
                                          data={ data.rows }
                                          columns={ data.columns }
                                          expandRow={ expandRow }
                                          pagination={ paginationFactory(options) }

                                        />
                                    </CardBody>
                                </Card>
                            </Col>

            </Row>
        </>
    );
}

export default Product;
