        console.log(products, "response");
        if (products.status === 200) {

            yield put(allProductsSuccess(products.data.data))



        } else if (products.status === 206) {
            yield put(allProductsError(products.data.message))

        }

    }
    catch (e) {
        console.log("e", e)

    }

}

