import {
    PRODUCT_ADD,
    PRODUCT_ADD_SUCCESS,
    PRODUCT_ADD_ERROR
}
    from "../actions"


const INIT_STATE = {

    products: [],
    loading: false,
    error: '',
    allProducts: [],
    selectedProduct: []
};
/* console.log("payloaddd",INIT_STATE.userProfile)
 */
const sellerProduct = (state = INIT_STATE, action) => {


    switch (action.type) {
        case PRODUCT_ADD:
            return { ...state, loading: true, error: '' };
        case PRODUCT_ADD_SUCCESS:
            return {
                ...state,
                loading: false,

            };
        case PRODUCT_ADD_ERROR:
            return {
                ...state,
                loading: false,
                error: action.error,
            };
               default:
            return { ...state };
    }
};
export default sellerProduct;
