import React, { useEffect } from "react";
import { Link, NavLink } from "react-router-dom"
import { connect } from "react-redux"
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit} from '@fortawesome/free-solid-svg-icons'
import {
    Row,
    Col,
    Card,
    CardBody,

    Button

} from 'reactstrap'
import { allProducts, allSeller } from "../../redux/product/actions"
import { getCurrentUser } from "../../helpers/Utils";
import BuyerNavbar from "./buyerNav"





const Buyer = ({ history, allProductsAction, allProduct, allSellerAction }) => {

    const getProductData = () => {
        const user_id = getCurrentUser().id
        allProductsAction(user_id, history)

    }
    const SellerData = ()=>{
        allSellerAction(history)
    }


    useEffect(() => {
        SellerData()
        getProductData()
    }, [])

    const data = {
        columns: [
            {
                dataField: 'id',
                text: 'No.'
            },
            {
                dataField: "Profile",
                text: "Profile"
            },
            {
                dataField: "name",
                text: "Username"
            },
            {
                dataField: "email",
                text: "Email"
            },
            {
                dataField: "edit",
                text: "Action"
            },


        ],
        rows: allSellerData.map(seller=>(
            {

                id: seller.id,
                Profile:<img src={seller.picture} alt="seller profile picture"/>,
                name: product.username,
                email: product.email,
                ViewProfile: <NavLink to={`/seller-profile/${seller.id}`}><FontAwesomeIcon icon={faEdit} ></FontAwesomeIcon></NavLink>


            }

        )),
    }



    const options = {
        // pageStartIndex: 0,
        hideSizePerPage: false,
        hidePageListOnlyOnePage: false,
        sizePerPageList:
            [{
                text: '5th', value: 5
            }, {
                text: '10th', value: 10
            }, {
                text: 'All', value: data.rows.length
            }]

    };


    return (
        <>
        <BuyerNavbar/>

           {allProduct && allProduct.length > 0?
            <Row>

                <Col lg={9} className='mt-5 mx-auto'>
                    <Card>
                        <CardBody>


                            <h4 className="card-title mb-4">All Products</h4>

                            <BootstrapTable
                                keyField='id'
                                data={data.rows}
                                columns={data.columns}
                                pagination={paginationFactory(options)}

                            />
                        </CardBody>
                    </Card>
                </Col>

            </Row>
            :""}
        </>
    );
}


const mapStateToProps = ({ sellerProduct }) => {
    const { allProduct, allSellerData, loading } = sellerProduct;
    console.log("allSellerData", allSellerData)

    return { allSellerData, allProduct, loading };


};


export default connect(mapStateToProps, { allSellerAction: allSeller, allProductsAction: allProducts })(Buyer)
